package com.serveme.service.notification.api.controller;import java.util.logging.Logger;import javax.inject.Inject;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import com.serveme.service.notification.domain.Mail;import com.serveme.service.notification.service.MailService;@Controllerpublic class MailAPI extends NotificationAPIBase {    @Inject    protected MailService mailService;    public MailAPI() {        logger = Logger.getLogger(MailAPI.class.getName());    }    @ResponseBody    @RequestMapping(value = "/notification/mail/create", method = RequestMethod.POST, produces = "application/json")    public ResponseEntity acceptOrder(            @RequestBody Mail mail    ) {        mailService.registerNewEmail(mail);        return new ResponseEntity(HttpStatus.OK);    }}